import React, {Component} from 'react';
import Table from "../ReusableComp/Table";
import Card from './Card'
import DataScientist from "../information/DataConstants";

class Data extends Component {
    state = {
        SelectedSketch: {},
        sketches: []
    };
    columns =[
        {
            label:"Name",
            path:"title"
        },
        {
            label:"Profession",
            path:"profession"
        },
        {
            key: "Buttons1",
            content: item => {
                return (
                    <button
                        className="btn btn-primary btn-sm"
                        onClick={() => this.handleSelect(item)}
                    >
                        Select
                    </button>
                );
            }
        }
    ];
    componentDidMount() {
        this.setState({ sketches: DataScientist() });
        this.setState({SelectedSketch:DataScientist()[0]})
    }
    render() {
        return (
            <>
                <div className="row">
                    <Table columns={this.columns} data={this.state.sketches}/>
                </div>
                <div className="row">
                    <div className="m-2 col">
                        {this.RenderCard()}
                    </div>
                </div>
            </>
        );
    }
    RenderCard (){
        const {SelectedSketch} =this.state;
        if(Object.keys(SelectedSketch).length>0)
            return (<Card data = {SelectedSketch} Footer={false}/>);
        else
            return null;
    }
    handleSelect(item) {
        this.setState({ SelectedSketch: item });
    }
}

export default Data;
