import React, { Component } from "react";
import { connect } from "react-redux";
import {selectSong} from "../actions";

class SongList extends Component {
  render() {
    console.log(this.props);
    const { songs ,selectSong } = this.props;
    const array = [...songs];
    // console.log(array);
    return (
      <div>
        <table className="table table-sm table-condensed table-responsive-sm">
          <thead className="table-dark">
            <tr>
              <th>Title</th>
              <th>Artist</th>
              <th>Duration</th>
              <th/>
            </tr>
          </thead>
          <tbody>
            {array.map((item, index) => {
              return(<tr key={index}>
                <td>{item.title}</td>
                <td>{item.artist}</td>
                <td>{item.duration}</td>
                <td>
                  <button
                      className="btn btn-dark btn-sm"
                      
                  >
                    Select
                  </button>
                </td>
              </tr>);
            })}
          </tbody>
        </table>
      </div>
    );
  }
}

const mapStateToProps = state => {
  // console.log(state); //what does connect does to this function
  return {
    songs: state.song
  };
};

export default connect(mapStateToProps, {
      selectSong:selectSong
    })(SongList);
