import React from 'react';

const Spinner = ({message}) => {
    return (
        <div className="mt-5">
            <div className="fontify display-3 mt-5 align-middle">{message}</div>
            <div className="ui  large  active centered inline loader align-middle "/>
        </div>
    );
};
Spinner.defaultProps={
  message:"Loading"
};

export default Spinner;
