import React, { Component } from "react";
import Sim1 from "./sketch1";
import P5Wrapper from "./P5Wrapper";

class Simulations extends Component {
  state = {
    start:false,
    reset:false

  };

  render() {
    return (
      <React.Fragment>
        <P5Wrapper sketch={Sim1} p5Props={{ width1: 100, height: 100 }} />
        <input
            className="btn btn-dark m-2"
            type="button"
            name="start"
            value={this.state.start}
        >Start</input>
        <input
            className="btn btn-dark m-2"
            type="button"
            name="reset"
            value={this.state.reset}
        >Reset</input>
      </React.Fragment>
    );
  }
  ButtonClick = (event)=>{
      const {target:{value,name}}=event;
      this.setState({[name]:value});
  }
}

export default Simulations;
