import React from "react";

const Input = ({ name, label, value, error, onChange,type }) => {
  return (
    <div className="form-group">
      <label htmlFor={name} className="fontifyr">
        {label}
      </label>
      <input
        value={value}
        id={name}
        type={type}
        className="form-control"
        onChange={onChange}
        name={name}
      />

      {error && (
        <span className="badge-pill badge-danger fontify">{error}</span>
      )}
    </div>
  );
};

export default Input;
