import { combineReducers } from "redux";


export const songReducers = () => {
  return [
    {
      title: "Smells like teen spirit",
      artist: "Nirvana",
      duration: "4.05"
    },
    {
      title: "Battery ",
      artist: "Metallica",
      duration: "6.08"
    },
    {
      title: "Unforgiven ",
      artist: "Metallica",
      duration: "5.08"
    },
    {
      title: "Moth into flame ",
      artist: "Metallica",
      duration: "5.08"
    }
  ];
};

const SongSelectReduccer = (SelectedOne = null, action) => {
  if (action.type === "SONG_SELECTED") return action.payload;

  return SelectedOne;
};
const SongDeleteReducer = (NewSongs =songReducers(), action) =>{
  if(action.type === "SONG_DELETED")
  {
    return NewSongs.filter((m)=>{
       return (m!==action.payload.song)
    });

  }
  return NewSongs;
};

export default combineReducers({
    song:songReducers,
    SongSelected:SongSelectReduccer,
    SongDeleted:SongDeleteReducer
});
