import React, { Component } from "react";
import Table from "../Reusable/Table";
import { connect } from "react-redux";
import { fetchStreams } from "../../actions";
import { Link } from "react-router-dom";

class TableStream extends Component { //this table stream will come with pagination
  componentDidMount() {
    this.props.fetchStreams();
  }
  renderCreateStreamB() {
    if (this.props.isSignedIn) {
      return (
        <Link to="streams/new">
          <button className="btn btn-dark btn-lg m-5">Create Stream</button>
        </Link>
      );
    }
  }
  columns = [
    { path: "title", label: "Title" },
    { path: "description", label: "Description" },
    {
      key: "Buttons",
      content: stream => {
        if (stream.userId === this.props.currentUserId)
          return (
            <Link to={`/streams/delete/${stream.id}`}>
              <button className="btn btn-danger btn-sm">Delete</button>
            </Link>
          );
      }
    },
    {
      key: "Buttons1",
      content: stream => {
        if (stream.userId === this.props.currentUserId)
          return (
            <Link to={`/streams/edit/${stream.id}`}>
              <button className="btn btn-dark btn-sm">Edit</button>
            </Link>
          );
      }
    }
  ];

  render() {
    // console.log(this.props.data); //this proves that we already fetched the data from rhe rest API
    return (
      <div className="container">
        <Table columns={this.columns} data={this.props.streams} />
        {this.renderCreateStreamB()}
      </div>
    );
  }
}

const mapStatetoProps = state => {
  return {
    streams: Object.values(state.STreams), //converting the object to array
    isSignedIn: state.authReducer.isSignedIn,
    currentUserId: state.authReducer.userId
  };
};
export default connect(
  mapStatetoProps,
  { fetchStreams }
)(TableStream);
