import React, {Component} from 'react';

class ImageCard extends Component {
    constructor(props) {
        super(props);
        this.imageRef=React.createRef();
    }

    componentDidMount() {
        console.log(this.imageRef);
    }


    render() {
        const {urls,description}=this.props.images;
        return (
           <React.Fragment>
               <img
                   ref={this.imageRef}
                   src={urls.regular}
                   alt={description}
               />
           </React.Fragment>
        );
    }
}

export default ImageCard;
