import React, { Component } from "react";
import Like from "./Like";
import { paginate } from "../utility/paginate";

class TableBody extends Component {
  render() {
    return <tbody>{this.body()}</tbody>;
  }
  body = () => {
    const {
      movies: allMovies,
      PageSize,
      currentPage,
      selectedGenre
    } = this.props;
    const filtered_movies = allMovies.filter(m => {
      return m === selectedGenre;
    });

    const movies1 = paginate(filtered_movies, currentPage, PageSize);
    return movies1.map(movie => {
      return (
        <tr key={movie._id}>
          <td className="fontify">{movie.title}</td>
          <td className="fontify">{movie.genre.name}</td>
          <td className="fontify">{movie.numberInStock}</td>
          <td className="fontify">{movie.dailyRentalRate}</td>
          <td className="fontify">
            <button
              className="btn btn-info m-2"
              onClick={() => this.props.onLike(movie)}
            >
              <Like liked={movie.liked} />
            </button>
            <button
              className="btn btn-danger btn-sm"
              onClick={() => this.props.onDelete(movie._id)}
            >
              Delete
            </button>
          </td>
        </tr>
      );
    });
  };
}

export default TableBody;
