import React, { Component } from "react";
import Sim4 from "./sketch4";
import P5Wrapper from "./P5Wrapper";
import FormButton from "../ReusableComp/FormButton";
import FormBodyRow from "../ReusableComp/FormBodyRow";

class Simulations4 extends Component {
  state = {
    start: false,
    reset: false,
    radius:25,
    xspeed:20,
    yspeed:10,
      Sticky:false,
      xPos:50,
      YPos:200
  };


  render() {
    const {start ,reset ,radius,xspeed,yspeed} = this.state;
    return (
      <>
        <div className="row">
          <div className="col-xl-8">
            <P5Wrapper sketch={Sim4} p5Props={this.state} />
            <FormButton start={start} reset={reset} onClickR={this.ButtonClickR} onClickS={this.ButtonClick}/>
          </div>
          <div className="col-xl-4">

            <table className="table table-responsive mt-2 fontify">
              <thead className="thead-dark">
              <tr>
                <td>Select</td>
                <td>Values</td>
                <td />
              </tr>
              </thead>
              <tbody>
              <FormBodyRow
                  name = {"radius"}
                  label={"Radius"}
                  OnMyChange={this.handleChange}
                  Info={{
                    min:10,
                    max:100,
                    value:radius,
                    type:"range"
                  }} />
              <FormBodyRow
                  name = {"xspeed"}
                  label={"Speed (X)"}
                  OnMyChange={this.handleChange}
                  Info={{
                    min:0,
                    max:50,
                    value:xspeed,
                    type:"range"
                  }} />
              <FormBodyRow
                  name = {"yspeed"}
                  label={"Speed (Y)"}
                  OnMyChange={this.handleChange}
                  Info={{
                    min:0,
                    max:50,
                    value:yspeed,
                    type:"range"
                  }} />
              <FormBodyRow
                  name = {"xPos"}
                  label={"Position (X)"}
                  OnMyChange={this.handleChange}
                  Info={{
                      min:50,
                      max:window.innerWidth /1.5,
                      value:this.state.xPos,
                      type:"range"
                  }} />
              <FormBodyRow
                  name = {"YPos"}
                  label={"Position (Y)"}
                  OnMyChange={this.handleChange}
                  Info={{
                      min:50,
                      max:window.innerHeight / 1.5,
                      value:this.state.YPos,
                      type:"range"
                  }} />
              <tr>
                  <td>Drawing</td>
                  <td>
                      <select onChange={this.handleSelect} disabled={start}>
                          <option value={true}>UnSticky</option>
                          <option value={false}>Sticky</option>

                      </select>
                  </td>
              </tr>
              </tbody>
            </table>
          </div>
        </div>
      </>
    );
  }

  ButtonClick = event => {
    this.setState({ start: true });
    this.setState({ reset: false });
  };
  ButtonClickR = event => {
    this.setState({ start: false });
    this.setState({ reset: true });
  };

  handleChange = event => {
    const {
      target: { name, value }
    } = event;
    //console.log(value);
    this.setState({ [name]: value });
  };
    handleSelect = event => {
        const {
            target: { value }
        } = event;
        if (value === "true") this.setState({ Sticky: false });
        else this.setState({ Sticky: true });
    };
}

export default Simulations4;
