import React from 'react';

const FormButton = ({start,onClickS,onClickR,reset,disapear}) => {
    if(!disapear) {
        return (
            <form>
                <button
                    className="btn btn-dark btn-large m-2"
                    type="button"
                    name="start"
                    value={start}
                    onClick={onClickS}
                    disabled={start}
                >
                    Start
                </button>
                <button
                    className={start ? "btn  btn-primary m-2" : "btn btn-dark m-2"}
                    type="button"
                    name="reset"
                    value={reset}
                    onClick={onClickR}
                >
                    Reset
                </button>
            </form>
        );
    }
    else return null; //or we should return the error here it is a nice idea
};

export default FormButton;
