import React, { Component } from "react";

class LoginForm extends Component {
  state = {
    account: { nameMovie: "", genre: "" }
  };
  username = React.createRef();
  /* componentDidMount() {
      this.username.current.focus();
  }*/

  handleSubmit = e => {
    e.preventDefault(); //it will prevent this behavior
    console.log(this.username.current.value);
    //do not overuse them but as long as you are using them for animation and
    //stuff it is ok
  };
  handleChange = ({currentTarget:ii}) => {
    const account = { ...this.state.account };
      account[ii.name] = ii.value;
    this.setState({ account });
  };
  render() {
      const {account}=this.state;
    return (
      <div className="backk">
        <br />
        <h1>Information</h1>
        <form onSubmit={this.handleSubmit}>
          <div className="form-group">
            <label htmlFor="username" className="fontifyr">
              Name
            </label>
            <input
              value={account.nameMovie}
              autoFocus
              ref={this.username}
              id="nameMovie"
              type="text"
              className="form-control"
              onChange={this.handleChange}
              name="nameMovie"
            />
          </div>
          <div className="form-group">
            <label htmlFor="password" className="fontifyr">
              Genre
            </label>
            <input
              value={account.genre}
              id="genre"
              name="genre"
              type="text"
              className="form-control"

              onChange={this.handleChange}
            />
          </div>
          <button className="btn btn-primary">Submit</button>
        </form>
      </div>
    );
  }
}

export default LoginForm;
