import React, { Component } from "react";
import P5Wrapper from "./Simulations1";
import simulu from "./sketch3";

class Simulations3 extends Component {
  state = {
    start: false,
    reset: false
  };
  render() {
    return (
      <div className="row">
        <div className="col-9">
          <P5Wrapper sketch={simulu} p5Props={this.state} />
        </div>
        <div>
          <form>
            <button
              className="btn btn-dark m-2"
              type="button"
              name="start"
              value={this.state.start}
              onClick={this.ButtonClick}
            >
              Start
            </button>
            <button
              className="btn btn-dark m-2"
              type="button"
              name="reset"
              value={this.state.reset}
              onClick={this.ButtonClickR}
            >
              Reset
            </button>
          </form>
        </div>
      </div>
    );
  }
  ButtonClick = event => {
    this.setState({ start: true });
    this.setState({ reset: false });
  };
  ButtonClickR = event => {
    this.setState({ start: false });
    this.setState({ reset: true });
  };
  handleChange = event => {
    const {
      target: { name, value }
    } = event;
    //console.log(value);
    this.setState({ [name]: value });
  };
}

export default Simulations3;
