import React, { Component } from "react";
import Table from "../Reusable/Table";
import {connect} from "react-redux";
import {fetchStreams} from "../../actions";
import {Link} from "react-router-dom";

class TableStream extends Component {

  componentDidMount() {
    this.props.fetchStreams();
  }

  columns = [
    {path:"title" ,label: "Title" },
    {path:"description" ,label: "Description" },
    {
      key: "Buttons",
      content: (stream) => {
        return (
            <Link to={`/streams/delete/${stream.id}`}>
            <button
                className="btn btn-danger btn-sm"
            >Delete</button>
            </Link>
        );
      }
    },
    {
      key: "Buttons1",
      content: () => {
        return (
            <Link to={`/streams/delete/${stream.id}`}>
              <button
                  className="btn btn-dark btn-sm"
              >Edit
              </button>
            </Link>
        );
      }
    }
  ];

  render() {
    // console.log(this.props.data); //this proves that we already fetched the data from rhe rest API
    return <Table columns={this.columns} data={this.props.streams} />;
  }
}

const mapStatetoProps = state => {
  return {
    streams: Object.values(state.STreams), //converting the object to array
    isSignedIn: state.authReducer.isSignedIn
  };
};
export default connect(
    mapStatetoProps,
    { fetchStreams }
)(TableStream);
