    this.setState({ selectedVideo: vii });
  };
  render() {
    return (
      <React.Fragment>
        <NavBar>
          <SearchBar onsubmit={this.handleSubmit} />
        </NavBar>
        <br />

          <div className="row">
            <div className="col-7">
              <VideoDetail video={this.state.selectedVideo} />
            </div>
            <div className="col-5">
              <VideoList
                videos={this.state.videos}
                onVideoSelect={this.handleSelect}
              />
            </div>
          </div>

      </React.Fragment>
    );
  }
}

export default App;
