import React, { Component } from "react";
import P5Wrapper from "./P5Wrapper";
import Sim2 from "./sketch2";
import ReportCard from "./ReportCard";
import FormButton from "../ReusableComp/FormButton";
import FormBodyRow from "./Simulations3";

class Simulations2 extends Component {
  state = {
    start: false,
    reset: false,
    degree: 30,
    radius: 25,
    velocity: 10,
    Gravitational: 9.9
  };
  col = () => {
    return {
      degree: this.state.degree,
      radius: this.state.radius,
      velocity: this.state.velocity,
      Gravitational: this.state.Gravitational
    };
  };
fcol = ()=>{
 return{
   velocity:10
 }
};
  render() {
    const { start ,reset } = this.state;
    return (
      <>
        <div className="row">
          <div className="col-8">
            <P5Wrapper sketch={Sim2} p5Props={this.state} />
            <FormButton start={start} reset={reset} onClickR={this.ButtonClickR} onClickS={this.ButtonClick}/>
          </div>

          <div className="col-4">
            <table className="table table-responsive mt-2 fontify">
              <thead className="thead-dark">
                <tr>
                  <td>Select</td>
                  <td>Values</td>
                  <td />
                </tr>
              </thead>
              <tbody>
              <FormBodyRow
                  label={"Degree"}
                  OnMyChange={this.handleChange}
                  name={"degree"}
                  Info={{
                    min:0,
                    max:90,
                    value:this.state.degree,
                    type:"range"
                  }}
              />
                <FormBodyRow
                    label={"Radius"}
                    OnMyChange={this.handleChange}
                    name={"radius"}
                    Info={{
                      min:25,
                      max:75,
                      value:this.state.radius,
                      type:"range"
                    }}
                />
                <tr>
                  <td>Velocity</td>
                  <td>
                    <input
                      min={5}
                      max={30}
                      type="range"
                      name="velocity"
                      value={this.state.velocity}
                      onChange={this.handleChange}
                      disabled={start}
                    />
                  </td>
                  <td>{this.state.velocity}</td>
                </tr>
                <FormBodyRow
                    label={"Gravitational"}
                    OnMyChange={this.handleChange}
                    name={"Gravitational"}
                    Info={{
                      min:5,
                      max:20,
                      value:this.state.Gravitational,
                      type:"range",

                    }}
                />
              </tbody>
            </table>

          </div>
        </div>
        <div className="card-deck"><ReportCard info={this.col()} name="Blue"  title = "Initial Information"/>{this.renderFinal()}</div>

      </>
    );
  }
  renderFinal = () => {
    if (this.state.start)
      return (
          <>
            <ReportCard
                info={this.fcol()}
                name="Blue"
                title="After The Motion"
            />

          </>
      );
    else return null;
  };
  ButtonClick = event => {
    const { value, name } = event.target;
    this.setState({ start: true });
    this.setState({ reset: false });
  };
  ButtonClickR = event => {
    const { value, name } = event.target;
    this.setState({ start: false });
    this.setState({ reset: true });
  };
  handleChange = event => {
    const {
      target: { name, value }
    } = event;
    //console.log(value);
    this.setState({ [name]: value });
  };

}

export default Simulations2;
