import React from "react";
import { connect } from "react-redux";
import { fetchStreams } from "../../actions";

class StreamList extends React.Component {
  componentDidMount() {
    this.props.fetchStreams(); //in the action creator which will give us an array of fetched data
  }

  renderStream = () => {
    const { streams } = this.props;
    return streams.map(stream => {
      return (
        <div className="item" key={stream.id}>
          <i className="large middle aligned icon camera"/>
          <div className="content">
            {stream.title}
            <div className="description">{stream.description}</div>
          </div>
        </div>
      );
    });
  };
  render() {
    return <div className="ui celled list">{this.renderStream()}</div>;
  }
}
const mapStatetoProps = state => {
  return {
    streams: Object.values(state.STreams),//converting the object to array
    userId:state.authReducer.userId
  };
};
export default connect(
  mapStatetoProps,
  { fetchStreams }
)(StreamList);
