import React, { Component } from "react";
import { getMovies } from "../services/fakeMovieService";
import { getGenres } from "../services/fakeGenreService";
import Pagination from "./pagination";
import ListGroup from "./ListGroup";
import MoviesTable from "./MoviesTable";
import _ from "lodash";
import { paginate } from "../utility/paginate";
import { Link } from "react-router-dom";
import Searchbox from "./Searchbox";

class Movies extends Component {
  state = {
    movies: [], //not the right way by the way
    genres: [],
    PageSize: 4,
    currentpage: 1,
    sortColumn: { path: "title", order: "asc" },
    search: ""
  };

  componentDidMount() {
    const genres = [{ name: "All Genres", _id: "" }, ...getGenres()];
    this.setState({ movies: getMovies(), genres: genres });
  }
  getPageData = () => {
    const {
      selectedGenre,
      movies: allMovies,
      currentpage,
      PageSize,
      sortColumn,
        search
    } = this.state;

    if(this.state.search!=="")
    {
      let regular=new RegExp(this.state.search,'i');

      let  RegMov=this.state.movies.filter(
          (m)=> (!m.title.search(regular))
      );
      const filtered_movies =
          selectedGenre && selectedGenre._id
              ? RegMov.filter(m => m.genre._id === selectedGenre._id)
              : RegMov;

      const sorted = _.orderBy(
          filtered_movies,
          [sortColumn.path],
          [sortColumn.order]
      );

      const movies = paginate(sorted, currentpage, PageSize);
      return { movies, SomeLength: filtered_movies.length };

    }
    else
    {
    const filtered_movies =
      selectedGenre && selectedGenre._id
        ? allMovies.filter(m => m.genre._id === selectedGenre._id)
        : allMovies;

    const sorted = _.orderBy(
      filtered_movies,
      [sortColumn.path],
      [sortColumn.order]
    );

    const movies = paginate(sorted, currentpage, PageSize);
    return { movies, SomeLength: filtered_movies.length };
    }
  };

  render() {
    /*let regular=new RegExp(this.state.search,'i');

    const  RegMov=this.state.movies.filter(
        (m)=> (!m.title.search(regular))
    );
    console.log(RegMov);*/

    const { currentpage, PageSize, sortColumn,search} = this.state;

    const { movies, SomeLength } = this.getPageData();

    if (SomeLength === 0 && search==="" )
      return (
          <React.Fragment>
            <h1 className="fontify badge badge-pill badge-danger">
            no more movies in Database
          </h1>
          </React.Fragment>
      );
    else
      return (
        <div className="row">
          <div className="col-4">
            <ListGroup
              items={this.state.genres}
              onItemSelect={this.handleGenreSelect}
              selectedItem={this.state.selectedGenre}
            />
          </div>

          <div className="col">
            <br />
            <span className="fontify alert  alert-info d-inline-block">
              Showing <span className="badge badge-light">{SomeLength}</span>{" "}
              movies in Database
            </span>
            <br />
            <Link to="/movies/new">
              <button className="btn btn-primary btn-sm fontify">
                New Movie
              </button>
            </Link>
            <br />

            <Searchbox onChange={this.handleSearchChange} />
            <br />
            {/*{
              (search==="") ?(<React.Fragment><MoviesTable
              movies={movies}
              handleDelete={this.handleDelete}
              handleLike={this.handleLike}
              onSort={this.handleSort}
              sortColumn={sortColumn}
              onLike={this.handleLike}
              onDelete={this.handleDelete}
              />
              <Pagination
              itemsCount={SomeLength}
              PageSize={PageSize}
              onPageChange={this.handlePageChange}
              currentPage={currentpage}
              /></React.Fragment>):(
            <MoviesTable
              movies={RegMov}
              handleDelete={this.handleDelete}
              handleLike={this.handleLike}
              onSort={this.handleSort}
              sortColumn={sortColumn}
              onLike={this.handleLike}
              onDelete={this.handleDelete}
            />)

            }*/}
            <MoviesTable
                movies={movies}
                handleDelete={this.handleDelete}
                handleLike={this.handleLike}
                onSort={this.handleSort}
                sortColumn={sortColumn}
                onLike={this.handleLike}
                onDelete={this.handleDelete}
            />
            <Pagination
                itemsCount={SomeLength}
                PageSize={PageSize}
                onPageChange={this.handlePageChange}
                currentPage={currentpage}
            />
          </div>
        </div>
      );
  }

  handleDelete = the_id => {
    const newarray = this.state.movies.filter(w => {
      return w._id !== the_id;
    });
    this.setState({ movies: newarray });
  };

  handleLike = object => {
    const newarray = [...this.state.movies];
    let index = newarray.indexOf(object);
    newarray[index].liked = !newarray[index].liked;
    this.setState({ movies: newarray });
  };

  handlePageChange = page => {
    this.setState({ currentpage: page });
  };

  handleGenreSelect = genre => {
    this.setState({ selectedGenre: genre, currentpage: 1 });
  };

  handleSort = ob => {
    this.setState({ sortColumn: ob });
  };
  handleSearchChange = (event) => {
    const {name,value}=event.target;
    this.setState({[name]:value,selectedGenre:null});
  };
}

export default Movies;
