import React, { Component } from "react";
import "./App.css";
import axios from "axios";

const apiEndpoints = "https://jsonplaceholder.typicode.com/posts";
class App extends Component {
  state = {
    posts: []
  };

  handleAdd = async () => {
    const obj = { title: "a", body: "b" };
    const { data: post } = await axios.post(apiEndpoints, obj); //this is for sending data to the server
    const pp = [post, ...this.state.posts];
    this.setState({ posts: pp });
  };

  handleUpdate = async (post) => {
    post.title = "Updated";
    await axios.put(apiEndpoints+'/'+post.id,post);
    const posts=[...this.state.posts];
    const index=posts.indexOf(posts);
    posts[index]={...post};
    this.setState(posts);
  };

  handleDelete = async post => {
    const originalPosts=this.state.posts;

    const posts=this.state.posts.filter((p)=>p.id!==post.id);
    this.setState({posts});
//this is the optimistic way we are deleting then we are calling forchnage then
    //giving the message to the database
    try {
      const bb=await axios.delete(apiEndpoints + '/999');
      // throw new Error('blig'); to see if out catch will get  back our posts
      console.log(bb);
    } catch (e) {
      /*if(e.response && e.response.status===404)
        alert('this post has already been deleted');
      else
      {
        console.log('login the error',e);
        alert('Unexpected Error has occurred');
      }

*/console.log(e.response);
      this.setState({posts:originalPosts});
    }
  };

  async componentDidMount() {
    /*const promise=axios.get('https://jsonplaceholder.typicode.com/posts');
    console.log(promise);
    const respone=await promise;
    console.log(respone);*/
    const { data: posts } = await axios.get(apiEndpoints);//getting elements from server
    this.setState({ posts });
  }

  render() {
    return (
      <React.Fragment>
        <button className="btn btn-primary" onClick={this.handleAdd}>
          Add
        </button>
        <table className="table">
          <thead>
            <tr>
              <th>Title</th>
              <th>Update</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>
            {this.state.posts.map(post => (
              <tr key={post.id}>
                <td>{post.title}</td>
                <td>
                  <button
                    className="btn btn-info btn-sm"
                    onClick={() => this.handleUpdate(post)}
                  >
                    Update
                  </button>
                </td>
                <td>
                  <button
                    className="btn btn-danger btn-sm"
                    onClick={() => this.handleDelete(post)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </React.Fragment>
    );
  }
}

export default App;
