import { combineReducers } from "redux";


export const songReducers = () => {
  return [
    {
      title: "Smells like teen spirit",
      artist: "Nirvana",
      duration: "4.05"
    },
    {
      title: "Battery ",
      artist: "Metallica",
      duration: "6.08"
    },
    {
      title: "Unforgiven ",
      artist: "Metallica",
      duration: "5.08"
    },
    {
      title: "Moth into flame ",
      artist: "Metallica",
      duration: "5.08"
    }
  ];
};

const SongSelectReduccer = (SelectedOne = null, action) => {
  if (action.type === "SONG_SELECTED") return action.payload;

  return SelectedOne;
};
const SongDeleteReducer = (SelectedOne = null, action) =>{
  if(action.type === "SONG_DELETED")
  {
    const array=songReducers();
    //const index=array.indexOf(SelectedOne);
    array.filter((m)=>{
      return (m!==SelectedOne)
    });
    return array;
  }
  return SelectedOne;
};

export default combineReducers({
    song:songReducers,
    SongSelected:SongSelectReduccer,
  SongDeleted:SongDeleteReducer
});
