import React, { Component } from "react";
import P5Wrapper from "./P5Wrapper";
import Sim2 from "./sketch2";

class Simulations2 extends Component {
  state = {
    start: false,
    reset: false,
    degree: 30,
    radius: 25,
    velocity: 10,
    Gravitational: 9.9
  };
  render() {
    return (
      <div className="row">
        <div className="col-9">
          <P5Wrapper sketch={Sim2} p5Props={this.state} />
        </div>

        <div className="col-3">
          <table className="table table-responsive mt-2 fontify">
            <thead className="thead-dark">
              <tr>
                <td>Select</td>
                <td>Values</td>
                <td />
              </tr>
            </thead>
            <tbody>
              <tr>
                <td >Degree</td>
                <td>
                  <input
                    type="range"
                    name="degree"
                    value={this.state.degree}
                    onChange={this.handleChange}
                  />
                </td>
                <td>{this.state.degree}</td>
              </tr>
              <tr>
                <td >Radius</td>
                <td>
                  <input
                    type="range"
                    name="radius"
                    value={this.state.radius}
                    onChange={this.handleChange}
                  />
                </td>
                <td>{this.state.radius}</td>
              </tr>
              <tr>
                <td>Velocity</td>
                <td>
                  <input
                    type="range"
                    name="velocity"
                    value={this.state.velocity}
                    onChange={this.handleChange}
                  />
                </td>
                <td>{this.state.velocity}</td>
              </tr>
              <tr>
                <td>Gravitation</td>
                <td>
                  <input
                    type="range"
                    name="Gravitational"
                    value={this.state.Gravitational}
                    onChange={this.handleChange}
                  />
                </td>
                <td>{this.state.Gravitational}</td>
              </tr>
            </tbody>
          </table>
          <form>
            <button
              className="btn btn-dark m-2"
              type="button"
              name="start"
              value={this.state.start}
              onClick={this.ButtonClick}
            >
              Start
            </button>
            <button
              className="btn btn-dark m-2"
              type="button"
              name="reset"
              value={this.state.reset}
              onClick={this.ButtonClickR}
            >
              reset
            </button>
          </form>
        </div>
      </div>
    );
  }
  ButtonClick = event => {
    const { value, name } = event.target;
    this.setState({ start: true });
    this.setState({ reset: false });
  };
  ButtonClickR = event => {
    const { value, name } = event.target;
    this.setState({ start: false });
    this.setState({ reset: true });
  };
  handleChange = event => {
    const {
      target: { name, value }
    } = event;
    //console.log(value);
    this.setState({ [name]: value });
  };
}

export default Simulations2;
