import React from "react";
import infoSketches from "../information/MySketches";
import Card from "./Card";
import Table from "../ReusableComp/Table";

class Simulations extends React.Component {
  state = {
    SelectedSketch: {},
    sketches: []
  };
  componentDidMount() {
    this.setState({ sketches: infoSketches() });
  }

  columns = [
    {
      label: "Title",
      path: "title"
    },
    {
      label: "Catagory",
      path: "Catagory"
    },
    {
      key: "Buttons1",
      content: item => {
        return (
          <button
            className="btn btn-primary btn-sm"
            onClick={() => this.handleSelect(item)}
          >
            Select
          </button>
        );
      }
    }
  ];

  handleSelect(item) {
    this.setState({ SelectedSketch: item });
  }

  render() {
    console.log(this.state.SelectedSketch.length)
    return (
      <>
        <Table columns={this.columns} data={this.state.sketches} />
      </>
    );
  }
}

export default Simulations;
