import React, { Component } from "react";
import Sim4 from "./sketch4";
import P5Wrapper from "./P5Wrapper";

class Simulations4 extends Component {
  state = {
    start: false,
    reset: false
  };
  render() {
    return (
      <>
        <div className="row">
          <div className="col-xl-8">
            <P5Wrapper sketch={Sim4} p5Props={{}} />
          </div>
          <div className="col-xl-4" />
          <form>
            <button
                className="btn btn-dark m-2"
                type="button"
                name="start"
                value={start}
                onClick={this.ButtonClick}
                disabled={start}
            >
              Start
            </button>
            <button
                className={start ? "btn btn-primary m-2" : "btn btn-dark m-2"}
                type="button"
                name="reset"
                value={this.state.reset}
                onClick={this.ButtonClickR}
            >
              Reset
            </button>
          </form>
        </div>
      </>
    );
  }

  ButtonClick = event => {
    this.setState({ start: true });
    this.setState({ reset: false });
  };
  ButtonClickR = event => {
    this.setState({ start: false });
    this.setState({ reset: true });
  };
}

export default Simulations4;
