import React, { Component } from "react";
import "./App.css";
import axios from 'axios'

const apiEndpoints='https://jsonplaceholder.typicode.com/posts';
class App extends Component {
  state = {
    posts: []
  };

  handleAdd = async() => {
    const obj={ title:'a',body:'b' };
    const {data:post}=await axios.post(apiEndpoints,obj);
    const pp=[post,this.state.posts];
  };

  handleUpdate = post => {
    console.log("Update", post);
  };

  handleDelete = post => {
    console.log("Delete", post);
  };

  async componentDidMount() {
    /*const promise=axios.get('https://jsonplaceholder.typicode.com/posts');
    console.log(promise);
    const respone=await promise;
    console.log(respone);*/
    const {data:posts}=await axios.get(apiEndpoints);
    this.setState({posts});

  }

  render() {
    return (
      <React.Fragment>
        <button className="btn btn-primary" onClick={this.handleAdd}>
          Add
        </button>
        <table className="table">
          <thead>
            <tr>
              <th>Title</th>
              <th>Update</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>
            {this.state.posts.map(post => (
              <tr key={post.id}>
                <td>{post.title}</td>
                <td>
                  <button
                    className="btn btn-info btn-sm"
                    onClick={() => this.handleUpdate(post)}
                  >
                    Update
                  </button>
                </td>
                <td>
                  <button
                    className="btn btn-danger btn-sm"
                    onClick={() => this.handleDelete(post)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </React.Fragment>
    );
  }
}

export default App;
