import React, { Component } from "react";
import P5Wrapper from "./P5Wrapper";
import simulu from "./sketch3";
import ReportCard from "./ReportCard";

class Simulations3 extends Component {
  state = {
    start: false,
    reset: false,
    degree: 30,
    radius: 25,
    coefficient: 0.1,
    Gravitational: 10,
    mass: 25
  };
  col = () => {
    return {
      degree: this.state.degree,
      radius: this.state.radius,
      coefficient: this.state.coefficient,
      Gravitational: this.state.Gravitational
    };
  };

  fcol = () => {
    let theta = (this.state.degree * Math.PI) / 180;
    const { Gravitational, mass } = this.state;
    return {
      Vertical_Force: mass * Gravitational * Math.cos(theta),
      Horiznotal_Force: mass * Gravitational * Math.sin(theta),
      Acceleration: Gravitational * Math.sin(theta)
    };
  };
  render() {
    const { start } = this.state;
    return (
      <>
        <div className="row">
          <div className="col-8">
            <P5Wrapper sketch={simulu} p5Props={this.state} />
          </div>

          <div className="col-4">
            <table className="table table-responsive mt-2  fontify">
              <thead className="thead-dark">
                <tr>
                  <td>Select</td>
                  <td>Values</td>
                  <td />
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>Degree</td>
                  <td>
                    <input
                      min={1}
                      max={89}
                      type="range"
                      name="degree"
                      value={this.state.degree}
                      onChange={this.handleChange}
                      disabled={start}
                    />
                  </td>
                  <td>{this.state.degree}</td>
                </tr>
                <tr>
                  <td>Radius</td>
                  <td>
                    <input
                      min={25}
                      max={75}
                      type="range"
                      name="radius"
                      value={this.state.radius}
                      onChange={this.handleChange}
                      disabled={start}
                    />
                  </td>
                  <td>{this.state.radius}</td>
                </tr>
                <tr>
                  <td>Coef. Friction</td>
                  <td>
                    <input
                      min={0.1}
                      max={0.9}
                      type="range"
                      name="coefficient"
                      value={this.state.coefficient}
                      onChange={this.handleChange}
                      disabled={start}
                      step={0.1}
                    />
                  </td>
                  <td>{this.state.coefficient}</td>
                </tr>
                <tr>
                  <td>Gravitation</td>
                  <td>
                    <input
                      min={5}
                      max={20}
                      type="range"
                      name="Gravitational"
                      value={this.state.Gravitational}
                      onChange={this.handleChange}
                      disabled={start}
                      step={1}
                    />
                  </td>
                  <td>{this.state.Gravitational}</td>
                </tr>
                <tr>
                  <td>mass</td>
                  <td>
                    <input
                      min={1}
                      max={100}
                      type="range"
                      name="Gravitational"
                      value={this.state.mass}
                      onChange={this.handleChange}
                      disabled={start}
                      step={1}
                    />
                  </td>
                  <td>{this.state.mass}</td>
                </tr>
              </tbody>
            </table>
            <form>
              <button
                className="btn btn-dark m-2"
                type="button"
                name="start"
                value={start}
                onClick={this.ButtonClick}
                disabled={this.state.start}
              >
                Start
              </button>
              <button
                className={start ? "btn btn-primary m-2" : "btn btn-dark m-2"}
                type="button"
                name="reset"
                value={this.state.reset}
                onClick={this.ButtonClickR}
              >
                Reset
              </button>
            </form>
          </div>
        </div>
        <div className="row">
          <ReportCard
            info={this.col()}
            name="Blue"
            title="Initial information"
          />
          <ReportCard info={this.fcol()} name="Blue" title="After The Motion" />
        </div>
      </>
    );
  }
  ButtonClick = event => {
    this.setState({ start: true });
    this.setState({ reset: false });
  };
  ButtonClickR = event => {
    this.setState({ start: false });
    this.setState({ reset: true });
  };
  handleChange = event => {
    const {
      target: { name, value }
    } = event;
    //console.log(value);
    this.setState({ [name]: value });
  };
}

export default Simulations3;
