import { combineReducers } from "redux";

export const INTITIAL_SONGS ={ songs:[
  {
    title: "Smells like teen spirit",
    artist: "Nirvana",
    duration: "4.05",
    id: 1
  },
  {
    title: "Battery ",
    artist: "Metallica",
    duration: "6.08",
    id: 2
  },
  {
    title: "Unforgiven ",
    artist: "Metallica",
    duration: "5.08",
    id: 3
  },
  {
    title: "Moth into flame ",
    artist: "Metallica",
    duration: "5.08",
    id: 4
  }
]};

const SongSelectReduccer = (SelectedOne = null, action) => {
  if (action.type === "SONG_SELECTED") return action.payload;

  return SelectedOne;
};

const SongReducer = (state = INTITIAL_SONGS, action) => {
  if (action.type === "SONG_DELETED") {
    return state.songs.filter(m => {
      return m.id !== action.payload.id;
    });
  }

  return state;
};

export default combineReducers({
  SongSelected: SongSelectReduccer,
  SongReducer: SongReducer
});
