import React, {Component} from 'react';
import p5 from "p5";



class P5Wrapper extends Component {
    constructor(props) {
        super(props);

    }

    componentDidMount() {
        console.log(this.props);
      let canvas1 =new p5(this.props.sketch,'parent');//this is the best way
        
       canvas1.props=this.props.p5Props; //adding the props
    }
    shouldComponentUpdate(nextProps) {
        this.canvas1.props = nextProps.p5Props;
        return false
    }

    componentWillUnmount() {
        this.canvas1.remove();
    }

    render() {
        return (
            <React.Fragment>
                <div id="parent"/>
            </React.Fragment>
        );
    }

}

export default P5Wrapper;
