import React, { Component } from "react";
import Input from "./input";
import Joi from "joi-browser";

class LoginForm extends Component {
  state = {
    data: { nameMovie: "", genre: "" },
    error: {}
  };
  // username = React.createRef(); //use it with the animation
  /* componentDidMount() {
      this.username.current.focus();
  }*/
  schema = {
    nameMovie: Joi.string()
      .required()
        .min(5)
      .label("Name"),
    genre: Joi.string()
      .required()
        .min(5)
      .label("Genre")
  };
  validate = () => {
    const options = { abortEarly: false };
    const { error } = Joi.validate(this.state.data, this.schema, options);
    // console.log(error);
    if (!error) return null;

    const errors1 = {};
    for (let i1 of error.details) {
      errors1[i1.path[0]] = i1.message;
    }
    return errors1;
  };

  handleSubmit = e => {
    e.preventDefault(); //it will prevent this behavior
    // console.log(this.username.current.value);
    //do not overuse them but as long as you are using them for animation and
    //stuff it is ok
    const err = this.validate();
    this.setState({ error: err || {} });
    if (err) return; //in case of an error

    console.log("Submitted Successfully");
  };
  validateProperty = ({ name, value }) => {
    const obj = { [name]: value };
    const schema = { [name]: this.schema[name] };
    const { error } = Joi.validate(obj, schema);
    // console.log(Joi.validate(obj,schema)); //always output this to see it
    return error ? error.details[0].message : null;
  };

  handleChange = ({ currentTarget: ii }) => {
    const errors1 = { ...this.state.error };
    const errorMessage = this.validateProperty(ii);
    if (errorMessage) errors1[ii.name] = errorMessage;
    else delete errors1[ii.name];

    const data = { ...this.state.data };
    data[ii.name] = ii.value;
    this.setState({ data, error: errors1 });
  };

  render() {
    const { data, error } = this.state;
    return (
      <div>
        <br />
        <h1 className="backk">Information</h1>

      </div>
    );
  }
}

export default LoginForm;
