import React from "react";
import Form from "./Form";
import Joi from "joi-browser";

class LoginForm extends Form {
  state = {
    data: { nameMovie: "", genre: "" },
    error: {}
  };
  // username = React.createRef(); //use it with the animation
  /* componentDidMount() {
      this.username.current.focus();
  }*/
  schema = {
    nameMovie: Joi.string()
      .required()
      .min(5)
      .label("Name"),
    genre: Joi.string()
      .required()
      .min(5)
      .label("Genre")
  };
  doSubmit=()=>{
    console.log("Submitted");

  };

  render() {
    return (
      <div>
        <br />
        <h1 className="backk">Information</h1>
          <form onSubmit={this.handleSubmit}>
            {/*  <Input
                  name="nameMovie"
                  value={data.nameMovie}
                  label="Name"
                  onChange={this.handleChange}
                  error={error.nameMovie}
              />
              <Input
                  name="genre"
                  value={data.genre}
                  label="Genre"
                  onChange={this.handleChange}
                  error={error.genre}
              />*/}
              {this.renderInput("nameMovie","Movie")}
              {this.renderInput("genre","Genre")}
              {this.renderButton('Submit')}
          </form>
      </div>
    );
  }
}

export default LoginForm;
