import React, { Component } from "react";
import TableHead from "./TableHead";
import TableBody from "./TableBody";

class MoviesTable extends Component {
  raiseSort = path => {
    const ob = { ...this.props.sortColumn };
    if (ob.path === path) ob.order = ob.order === "asc" ? "desc" : "asc";
    else {
      ob.path = path;
      ob.order = "asc";
    }
    this.props.onSort(ob);
  };

  render() {
    const { movies, handleDelete, handleLike } = this.props;

    return (
      <table className="table  table-condensed table-sm">
        <TableHead SomeSort={this.raiseSort} />
        <TableBody
          movies={movies}
          onDelete={handleDelete}
          onLike={handleLike}
        />
      </table>
    );
  }
}

export default MoviesTable;
