import React, { Component } from "react";
import { getMovies } from "../services/fakeMovieService";
import { getGenres } from "../services/fakeGenreService";
import TableHead from "./TableHead";
import TableBody from "./TableBody";
import Pagination from "./pagination";
import ListGroup from "./ListGroup";

class Movies extends Component {
  state = {
    movies: [], //not the right way by the way
    genres: [],
    PageSize: 4,
    currentpage: 1
  };
  componentDidMount() {
    const genres=[{name:'All Genres'},...getGenres()];
    this.setState({ movies: getMovies(), genres:genres });
  }

  render() {
    const {selectedGenre,movies:allMovies}=this.state;

    const filtered_movies=
        selectedGenre && selectedGenre._id
        ?allMovies.filter(m=> m.genre._id===selectedGenre._id )
        :allMovies;

   // const { length } = this.state.movies;
    if (filtered_movies.length === 0)
      return (
        <h1 className="fontify badge badge-pill badge-danger">
          no more movies in Database
        </h1>
      );
    else
      return (
        <div className="row">
          <div className="col-3">
            <ListGroup
              items={this.state.genres}
              onItemSelect={this.handleGenreSelect}
              selectedItem={this.state.selectedGenre}
            />
          </div>

          <div className="col">
            <h1 className="fontify badge badge-pill badge-info">
              Showing {filtered_movies.length} movies in Database
            </h1>
            <table className="table  table-condensed table-sm">
              <TableHead />
              <TableBody
                movies={this.state.movies}
                PageSize={this.state.PageSize}
                currentPage={this.state.currentpage}
                onDelete={this.handleDelete}
                onLike={this.handleLike}
                selectedGenre={this.state.selectedGenre}
              />
            </table>
            <Pagination
              itemsCount={filtered_movies.length}
              PageSize={this.state.PageSize}
              onPageChange={this.handlePageChange}
              currentPage={this.state.currentpage}
            />
          </div>
        </div>
      );
  }

  handleDelete = the_id => {
    const newarray = this.state.movies.filter(w => {
      return w._id !== the_id;
    });
    this.setState({ movies: newarray });
  };
  handleLike = object => {
    const newarray = [...this.state.movies];
    let index = newarray.indexOf(object);
    newarray[index].liked = !newarray[index].liked;
    this.setState({ movies: newarray });
  };
  handlePageChange = page => {
    this.setState({ currentpage: page });
  };
  handleGenreSelect = genre => {
    this.setState({ selectedGenre: genre ,currentpage:1});
  };
}

export default Movies;
