import React, { Component } from "react";
import Input from "./input";
import Joi from 'joi-browser'

class LoginForm extends Component {
  state = {
    account: { nameMovie: "", genre: "" },
    error: {}
  };
  // username = React.createRef(); //use it with the animation
  /* componentDidMount() {
      this.username.current.focus();
  }*/
schema={
  nameMovie:Joi.string().required(),
  genre:Joi.string().required()
};
  validate = () => {
    const result=Joi.validate(this.state.account,this.schema,{});

    const { account } = this.state;
    const error1 = {};
    if (account.nameMovie.trim() === "") error1.nameMovie = "Name is Required";
    if (account.genre.trim() === "") error1.genre = "Genre is Required";

    return Object.keys(error1).length === 0 ? null : error1;
  };

  handleSubmit = e => {
    e.preventDefault(); //it will prevent this behavior
    // console.log(this.username.current.value);
    //do not overuse them but as long as you are using them for animation and
    //stuff it is ok
    const err = this.validate();
    this.setState({ error: err || {} });
    if (err) return; //in case of an error

    console.log("Submitted Successfully");
  };
  validateProperty = ({name,value}) => {
  if(name==='nameMovie')
    if(value.trim()==='')return 'Movie is Required';

   if(name==='genre')
     if(value.trim()==='')return 'Genre is Required'

  };
  handleChange = ({ currentTarget: ii }) => {
    const errors1={...this.state.error};
    const errorMessage=this.validateProperty(ii);
    if(errorMessage) errors1[ii.name]=errorMessage;
    else delete errors1[ii.name];

    const account = { ...this.state.account };
    account[ii.name] = ii.value;
    this.setState({ account,error:errors1 });
  };

  render() {
    const { account, error } = this.state;
    return (
      <div>
        <br />
        <h1 className="backk">Information</h1>
        <form onSubmit={this.handleSubmit}>
          <Input
            name="nameMovie"
            value={account.nameMovie}
            label="Name"
            onChange={this.handleChange}
            error={error.nameMovie}
          />
          <Input
            name="genre"
            value={account.genre}
            label="Genre"
            onChange={this.handleChange}
            error={error.genre}
          />
          <button className="btn btn-primary">Submit</button>
        </form>
      </div>
    );
  }
}

export default LoginForm;
