import React, { Component } from "react";

/*const TableHead = props => { //this was my way
  const { SomeSort } = props;
  return (
    <thead className="table-dark">
      <tr>
        <th className="fontifyr cur" onClick={() => SomeSort("title")}>
          Title
        </th>
        <th className="fontifyr cur" onClick={() => SomeSort("genre.name")}>
          Genre
        </th>
        <th className="fontifyr cur" onClick={() => SomeSort("numberInStock")}>
          Stock
        </th>
        <th className="fontifyr cur" onClick={() => SomeSort("dailyRentalRate")}>
          Rate
        </th>
        <th />
      </tr>
    </thead>
  );
};*/


class TableHead extends Component {

  raiseSort = path => {
    const ob = { ...this.props.sortColumn };
    if (ob.path === path) ob.order = ob.order === "asc" ? "desc" : "asc";
    else {
      ob.path = path;
      ob.order = "asc";
    }
    this.props.onSort(ob);
  };

  render() {

    return (
        <thead className="table-dark">
        <tr>
          {this.props.columns.map(column=>(
              <th
                  className="fontifyr cur"
                  key={column.path || column.key}
                  onClick={()=>this.raiseSort(column.path)}
              >{column.label}</th>
          ))}
        </tr>
        </thead>
    );
  }
}

export default TableHead;
