import React from 'react';

const Select = ({ name, label, options, error, ...rest }) => {
    return (
        <div className="form-group">
            <label htmlFor={name} className="fontifyr"> {label}</label>
            <select name={name} id={name} {...rest} className="form-control fontify">
                <option value="" />
                {options.map((option,index) =>  (
                    <option key={index} value={option._id}>
                        {option.name}
                    </option>
                ))}
            </select>
            {error && <span className="badge-pill badge-danger fontify">{error}</span>}
        </div>
    );
};
export default Select;
