import {
  SIGN_OUT,
  SIGN_IN,
  CREATE_STREAM,
  DELETE_STREAM,
  EDIT_STREAM,
  FETCH_STREAM,
  FETCH_STREAMS
} from "./types";

import streams from "../apis/streams";

export const SignIn = GoogleId => {
  return {
    type: SIGN_IN,
    payload: {
      //remeber in video is different
      id: GoogleId
    }
  };
};
export const SignOut = () => {
  return {
    type: SIGN_OUT
  };
};

export const createStream = formValues => {
  return async dispatch => {
    const response = await streams.post("/streams", formValues); //giving the form values so in order the
    dispatch({ type: CREATE_STREAM, payload: response.data });
  };
};

export const fetchStreams = ()=> {
  return async dispatch => {
    const response = await streams.get('/streams');//to get the array
    dispatch({type:FETCH_STREAMS,payload:response.data})
  };
};
