import React, { Component } from "react";
import  {Redirect} from "react-router-dom";

class SearchBar extends Component {
  state = {
    SearchBar: "",
    sumbit:false
  };


  render() {
    const { SearchBar } = this.state;
    return (
      <React.Fragment>
        <br />

        <form onSubmit={this.onSubmit} className="form-inline mt-2 fontify" action={SearchBar}>
          <label htmlFor="search">
            <i className="fas fa-search ml-1" aria-hidden="true" />
          </label>
          <input
            name="SearchBar"
            id="search"
            className="form-control form-control-lg ml-3 w-75 "
            type="text"
            aria-label="Search"
            onChange={this.OnInputChange}
            value={SearchBar}
          />

          <button type="submit" className="btn btn-primary m-2">Submit</button>
          {(this.state.sumbit===true) ? <Redirect from="/" to={`/${SearchBar}`}/>:null}




          <br />
        </form>
      </React.Fragment>
    );
  }

  OnInputChange = e => {
    const {
      target: { name, value }
    } = e;
    this.setState({ [name]: value });
  };

  onSubmit = event => {
    this.setState({sumbit:true});
    event.preventDefault();
    this.props.onSubmit(this.state.SearchBar);
  };
}

export default SearchBar;
