import React, { Component } from "react";
import Table from "../Reusable/Table";

class TableStream extends Component {
  columns = [
    { label: "Title" },
    { label: "Description" },
    {
      key: "Buttons",
      content: () => {
        return (
          <>
            <button className="btn btn-dark btn-sm">Delete</button>
            <button className="btn btn-danger btn-sm">Edit</button>
          </>
        );
      }
    }
  ];

  render() {
    // console.log(this.props.data); //this proves that we already fetched the data from rhe rest API
    return <Table columns={this.columns} data={this.props.data} />;
  }
}

export default TableStream;
