import React, { Component } from "react";
import p5 from "p5";
import {sketch1} from 'SketchOne'


class Simulations extends Component {
  constructor(props) {
    super(props);
  }

    componentDidMount() {
        let myp5 =new p5(Sim1,'parent');//this is the best way
    }

    render() {
    //here will go the p5


    return (
      <div className="container">
        <br />
        <div  id="parent" />
        <button className="btn btn-dark m-1" onClick={this.handlePlay}>Play</button>
        <button className="btn btn-dark m-1">Stop</button>
        <button className="btn btn-dark m-1">Reset</button>
      </div>
    );
  }


}

export default Simulations;
