import React from 'react';
import Form from "./Form"
import Joi from "joi-browser"
import { getGenres } from "../services/fakeGenreService";

class MovieForm extends Form  {
    state={
      data:{title:"",genre:"",NumberStock:"",rate:""},//lets for now forget the genre properties
        error:{},
        genres:[]
    };
    schema={
      title:Joi.string()
          .required()
          .label("Title"),
       NumberStock: Joi.number()
           .required()
           .min(0)
           .max(100),
        rate:Joi.number()
            .required()
            .min(0)
            .max(10),
        genre:Joi


    };
    doSubmit = () => {

        console.log("Submitted");
    };
    componentDidMount() {
        const genres = getGenres();
        this.setState({ genres });
    }

    render() {
        return (
            <React.Fragment>
                <br/>
            <h1 className="backk">Movie Form</h1>
                {this.renderInput("title","Title")}
                {this.renderSelect('genre',"Genre",this.state.genre)}
                {this.renderInput("NumberStock","Number in Stock")}
                {this.renderInput("rate","Rate")}
                {this.renderButton("Submit")}
            </React.Fragment>
        );
    }
}

export default MovieForm;
