import React, {Component} from 'react';
import TableHead from "./TableHead";
import TableBody from "./TableBody";

class MoviesTable extends Component {
        raiseSort=(path)=>{
            const ob={...this.state.sortColumn};
            if(ob.path===path)
                ob.order=(ob.order==='asc')?'desc':'asc';
            else
            {
                ob.path=path;
                ob.order='asc';
            }
           
        };

    render() {
        const {movies,handleDelete,handleLike,onSort} = this.props;

        return (
            <table className="table  table-condensed table-sm">
                <TableHead
                    onSort={onSort}
                />
                <TableBody
                    movies={movies}
                    onDelete={handleDelete}
                    onLike={handleLike}
                />
            </table>
        );
    }
}

export default MoviesTable;
