import React, { Component } from "react";

class LoginForm extends Component {
    username=React.createRef();

    handleSubmit=e=>{
      e.preventDefault();//it will prevent this behavior
      console.log(this.current.username.value);
    };
  render() {
    return (
      <div className="backk">
          <br/>
          <h1>Information</h1>
        <form onSubmit={this.handleSubmit}>
          <div className="form-group">
            <label htmlFor="username" className="fontifyr">Name</label>
            <input ref={this.username} id="username" type="text" className="form-control" />
          </div>
          <div className="form-group">
            <label htmlFor="password" className="fontifyr">Genre</label>
            <input id="password" type="text" className="form-control" />
          </div>
            <button className="btn btn-primary">Submit</button>
        </form>
      </div>
    );
  }
}

export default LoginForm;
