import React, {Component} from 'react';
import Joi from 'joi-browser';

class RegisterForm extends Component {
    state={
        username:"",password:"",name:""
    };
    schema={
      username:Joi.string()
          .required()
          .email()
          .label("Email")
          .alphanum(),
        password:Joi.string()
            .min(5)
            .required()
            .label("Password"),
        name:Joi.string()
            .required()
    };
    render() {

        return (
            <React.Fragment>
                <br/>
                <div className="fontifyr">
                    Hello
                </div>
            </React.Fragment>
        );
    }
}

export default RegisterForm;
