import React, { Component } from "react";
import p5 from "p5";

class Simulations extends Component {
  constructor(props) {
    super(props);
      this.myRef = React.createRef();
  }

  render() {
    //here will go the p5

    this.Sim1=new p5((p)=> {

        let x=20;
        p.setup = () => {
            p.createCanvas(600, 400,).parent(this.myRef.current);

        };
        p.draw = () => {
            p.background(0);
            p.fill(250);
            p.ellipse(x,20,50,50);
            x++;

        }
    });



    return (
      <div className="container">
          <br/>
        <div ref={this.myRef}/>
        <button className="btn btn-info">Play</button>
      </div>
    );
  }
}

export default Simulations;
