import React, { Component } from "react";
import { connect } from "react-redux";
import { selectSong , deleteSong } from "../actions";


class SongList extends Component {
  render() {
   // console.log(this.props); //the below map state to props will convert all that bullshit to props
   const { songs, selectSong, deleteSong } = this.props;
    const array = songs;
    console.log(array);
    return (
      <div>
        <table className="table table-hover table-sm table-condensed table-responsive-sm">
          <thead className="table-dark">
            <tr>
              <th>Title</th>
              <th />
              <th />
            </tr>
          </thead>
          <tbody>
            {array.map((item, index) => {
              return (
                <tr key={index}>
                  <td>{item.title}</td>
                  <td>
                    <button
                      className="btn btn-dark btn-sm"
                      onClick={() => selectSong(item)}
                    >
                      Select
                    </button>
                  </td>
                  <td>
                    <button
                      className="btn btn-dark btn-sm"
                      onClick={()=>this.props.deleteIt(item)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    );
  }
}

const mapStateToProps = state => {
   console.log(state); //what does connect does to this function
  return {
    songs: state.SongReducer.songs
  };
};
const mapDispatchToProps = dispatch => {
    return {
        deleteIt: (id) => {
            dispatch({type:'SONG_DELETED',id:id})
        }
    };
};

export default connect(
  mapStateToProps,mapDispatchToProps,
  {
    selectSong: selectSong,
    //this is what passes this function as component
  }
)(SongList);
