import React from 'react';

const ListGroup = (props) => {
    const {items,onItemSelect,textProperty,valueProperty,selectedItem}=props;

    return (
            <ul className="list-group-horizontal-sm mt-5 f1 d-inline-block">
                {items.map((item) =>
                    <li key={item[valueProperty]}
                        className={(selectedItem.profession === item.profession) ? "list-group-item active cur" : "list-group-item cur"}
                        onClick={() => onItemSelect(item)}>
                        {item[textProperty]}</li>)}
            </ul>

    );
};
ListGroup.defaultProps={
  textProperty:"profession",
    valueProperty:"id"
};
export default ListGroup;
