import React, {Component} from 'react';
import {getMovies} from "../services/fakeMovieService";
import TableHead from "./TableHead"
import TableBody from "./TableBody"


class Movies extends Component {
    state={
      movies:getMovies()//not the right way by the way
    };

    render() {
        const {length}=this.state.movies;
        if(length===0) return( <h1 className="fontify badge badge-pill badge-danger">no more movies in Database</h1>);
        else
        return (
            <React.Fragment>
                    <h1 className="fontify badge badge-pill badge-info">Showing {length} movies in Database</h1>
                    <table className="table table-sm m-3">
                        <TableHead/>
                        <TableBody
                            movies={this.state.movies}
                            onDelete={this.handleDelete}
                            onLike={this.handleLike}
                        />
                    </table>
            </React.Fragment>
        );
    }

    handleDelete=(the_id)=>{
        const newarray=this.state.movies.filter(
            (w)=>{return w._id!==the_id}
        );
        this.setState({movies:newarray});
    }
}

export default Movies;
