export default function  ( p ) {




    p.ButtonClicked=false;
    p.velocity1Intial = 10;
    p.velocity2Intial = -20;
    p.mass1 = 20;
    p.mass2 = 10;
    p.TMass = p.mass1 + p.mass2;
    p.height1 = 100;
    p.height2 = 150;
    p.width1 = 100;
    p.width2 = 100;
    p.x1 = p.width1 / 2;
    p.remainder =
        (1000 - p.width2 / 2 - p.width1 / 2) %
        (p.velocity1Intial + -p.velocity2Intial);
    p.x2 = 1000 - p.width2 / 2;
    p.DetectCollison = false;
    p.Inelastic = true;

    p.myCustomRedrawAccordingToNewPropsHandler = function (props) {
        if (props){
            console.log(props.width1);
        }
    };
    p.setup = () => {
        p.createCanvas(1000, 600);
    };

    p.draw = () => {
        p.background(0);
        if(p.ButtonClicked) {
            if (!p.Inelastic) CollisionDetectElastic();
            else {
                CollisonInelastic();
            }

            p.rectMode(p.CENTER);
            p.fill("green");
            p.rect(p.x1, 200, p.width1, p.height1, 20);
            p.fill(255, 204, 0);
            p.rect(p.x2, 200, p.width2, p.height2, 20);
        }
    };

    const CollisionDetectElastic = () => {
        let velocity2Final =
            (2 * p.mass1 * p.velocity1Intial +
                p.velocity2Intial * (p.mass2 - p.mass1)) /
            p.TMass;
        let velocity1Final =
            (2 * p.mass2 * p.velocity2Intial +
                p.velocity1Intial * (p.mass1 - p.mass2)) /
            p.TMass;

        /*   console.log("Vf2=", velocity2Final);
          console.log("V1f=", velocity1Final);*/

        let distance = p.dist(p.x1, 200, p.x2, 200);

        // console.log(distance);

        if (
            (distance <= (p.width1 + p.width2) / 2 + p.remainder &&
                distance <= (p.width1 + p.width2) / 2 - p.remainder) ||
            p.DetectCollison
        ) {
            p.DetectCollison = true;
            p.x1 += velocity1Final;
            p.x2 += velocity2Final;
        } else if (!p.DetectCollison) {
            p.x1 += p.velocity1Intial;
            p.x2 += p.velocity2Intial;
        }
    };

    const  CollisonInelastic = () => {
        let TVelocity =
            (p.mass1 * p.velocity1Intial + p.mass2 * p.velocity2Intial) / p.TMass;
        let distance = p.dist(p.x1, 200, p.x2, 200);
        if (
            (distance <= (p.width1 + p.width2) / 2 + p.remainder &&
                distance <= (p.width1 + p.width2) / 2 - p.remainder) ||
            p.DetectCollison
        ) {

            p.DetectCollison = true;
            p.x1 += TVelocity;
            p.x2 += TVelocity;
        }
        if (!p.DetectCollison) {
            p.x1 += p.velocity1Intial;
            p.x2 += p.velocity2Intial;
        }
    };

};
