import React from "react";
import infoSketches from "../information/MySketches";
import Card from "./Card";
import Table from "../ReusableComp/Table";

class Simulations extends React.Component {
  state = {
    SelectedSketch: {}
  };
  componentDidMount() {
    this.sketches = infoSketches();
  }

  CollectedCards = this.sketches.map((item, index) => {
    return <Card data={item} key={item.id} />;
  });
  columns = [
    {
      label: "Title",
      path: "title"
    },
    {
      label: "Catagory",
      path: "Catagory"
    },
    {
      key: "Buttons1",
      content: (item) => {
        return <button className="btn btn-primary btn-sm" onClick={()=>this.handleSelect(item)}>Select</button>;
      }
    }
  ];
    handleSelect(item){
        console.log(item);
    }
  render() {
    return (
      <>
        <Table columns={this.columns} data={this.sketches} />{" "}
      </>
    );
  }
}

export default Simulations;
