import React, { Component } from "react";
import { getMovies } from "../services/fakeMovieService";
import TableHead from "./TableHead";
import TableBody from "./TableBody";
import Pagination from "./pagination"
import ListGroup from "./ListGroup"

class Movies extends Component {
  state = {
    movies: getMovies(), //not the right way by the way
    PageSize:4,
    currentpage:1
  };

  render() {

    const { length } = this.state.movies;
    if (length === 0)
      return (
        <h1 className="fontify badge badge-pill badge-danger">
          no more movies in Database
        </h1>
      );
    else
      return (
        <div className="row">
          <div className="col-2">

          </div>
          <div className="col"><h1 className="fontify badge badge-pill badge-info">
            Showing {length} movies in Database
          </h1>
            <table className="table  table-condensed table-sm">
              <TableHead/>
              <TableBody
                  movies={this.state.movies}
                  PageSize={this.state.PageSize}
                  currentPage={this.state.currentpage}
                  onDelete={this.handleDelete}
                  onLike={this.handleLike}
              />
            </table>
            <Pagination
                itemsCount={this.state.movies.length}
                PageSize={this.state.PageSize}
                onPageChange={this.handlePageChange}
                currentPage={this.state.currentpage}
            /></div>
        </div>
      );
  }

  handleDelete = the_id => {
    const newarray = this.state.movies.filter(w => {
      return w._id !== the_id;
    });
    this.setState({ movies: newarray });
  };
  handleLike = object => {
    const newarray=[...this.state.movies];
    let index=newarray.indexOf(object);
    newarray[index].liked=!newarray[index].liked;
    this.setState({movies:newarray});

  };
  handlePageChange=(page)=>{
    this.setState({currentpage:page});

  }
}

export default Movies;
