import React, { Component } from "react";
import { Field, reduxForm } from "redux-form";
import Joi from "joi-browser";
import createValidator from "../../Validate/myValidation";

class StreamCreate extends Component {
  renderinput = formProps => {
   // console.log(formProps);
    const { input, label } = formProps;
    return (
      <div className="form-group m-2">
        <input
          {...input}
          type="text"
          className="form-control"
          id={label}
          placeholder={label}
        />
      </div>
    );
  };

  onSumbit = formValues => {
    console.log(formValues);
  };
  render() {
    // console.log(this.props);
    return (
      <form
        onSubmit={this.props.handleSubmit(this.onSumbit)}
        className="col-5 form-group"
      >
        <Field name="title" label="Title" component={this.renderinput} />
        <Field
          name="description"
          label="description"
          component={this.renderinput}
        />
        <button type="submit" className="btn btn-dark">
          Submit
        </button>
      </form>
    );
  }
}

const schema = {
  title: Joi.string()
    .required()
    .alphanum()
    .min(5)
    .label("Username"),
  description: Joi.string()
    .required()
    .min(5)
    .label("Password")
};

const validate = Form => {
  console.log(Joi.validate(Form, schema, { abortEarly: false }));
  const { error } = Joi.validate(Form, schema);
  if (error === null) return {};
  else {
   error.details.reduce((acc,value)=>{
     console.log(value);
   })
  }
};
export default reduxForm({
  form: "StreamCreate",
validate
})(StreamCreate);
